import React, { useState } from 'react';
import axios from 'axios';

const TransactionForm = () => {
    const [formData, setFormData] = useState({
        sender: '',
        receiver: '',
        amount: '',
        type: '',
        oldBalance: '',
        newBalance: '',
    });

    const [responseMessage, setResponseMessage] = useState('');

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();

        axios
            .post('/api/transaction', formData)
            .then((response) => {
                setResponseMessage(response.data.message);
            })
            .catch((error) => {
                setResponseMessage(error.response?.data?.message || 'Error processing transaction.');
            });
    };

    return (
        <div>
            <h2>Submit Transaction</h2>
            <form onSubmit={handleSubmit}>
                <input type="text" name="sender" placeholder="Sender" value={formData.sender} onChange={handleChange} required />
                <input type="text" name="receiver" placeholder="Receiver" value={formData.receiver} onChange={handleChange} required />
                <input type="number" name="amount" placeholder="Amount" value={formData.amount} onChange={handleChange} required />
                <input type="text" name="type" placeholder="Type" value={formData.type} onChange={handleChange} required />
                <input type="number" name="oldBalance" placeholder="Old Balance" value={formData.oldBalance} onChange={handleChange} required />
                <input type="number" name="newBalance" placeholder="New Balance" value={formData.newBalance} onChange={handleChange} required />
                <button type="submit">Submit</button>
            </form>
            {responseMessage && <p>{responseMessage}</p>}
        </div>
    );
};

export default TransactionForm;
